let input =
  ""

let explode s = String.to_seq s |> List.of_seq

let rec take xs n =
  match xs with
  | [] -> failwith "Take n too big"
  | head :: rest -> if n = 1 then [ head ] else head :: take rest (n - 1)

let all_uniq xs =
  xs
  |> List.for_all (fun x ->
         List.filter (fun y -> y == x) xs |> List.length == 1)

let rec marker_index xs i =
  if all_uniq (take xs 4) then i + 4 else marker_index (List.tl xs) i + 1

let part_one = marker_index (explode input) 0

let rec message_marker_index xs i =
  if all_uniq (take xs 14) then i + 14
  else message_marker_index (List.tl xs) i + 1

let part_two = message_marker_index (explode input) 0
